import Input from '../../UI/Input';
import classes from './MealItemForm.module.css'


const MealItemForm = () =>{

    const submitHandler = event =>{
        event.preventDefault();

    }

    return(
        <form onSubmit={submitHandler} className={classes.form}>
            <Input label="Amount" 
            
            input={{
                id:'amount',
                type:'number',
                min:'1',
                max:'5',
                step:'1',
                defaultValue:'1'
            }}/>
            <button>+ Add</button>
        </form>
    )
}

export default MealItemForm;
